import React,{useState} from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
const Login = () => {
  const[userName,setUserName]=useState("");
  const[password,setPassword]=useState("");
  
const submitLogin=(e)=>{
  e.preventDefault();
  if(validation()){// if it's return true
   toast.success("Success Sign In Now")
  }
  if(!validation()){
   
  }
}

const validation=()=>{
  let result=true;
  if(userName===null || userName===""){
    result=false;
    toast.warning("Please Enter UserName")
  }
  if(password===null || password===""){
    result=false;
    toast.warning("Please Enter Password")
  }
  return result;
}

  return (
    <div className="row">
    <div className="offset-lg-3 col-lg-6" style={{ marginTop: '12px' }}>
        <form onSubmit={submitLogin} className="container">
            <div className="card">
                <div className="card-header">
                    <h2>User Login</h2>
                </div>
                <div className="card-body">
                    <div className="form-group">
                        <label>User Name <span className="errmsg">*</span></label>
                        <input value={userName} onChange={e =>setUserName(e.target.value)} className="form-control"></input>
                    </div>
                    <div className="form-group">
                        <label>Password <span className="errmsg">*</span></label>
                        <input type="password" value={password} onChange={e =>setPassword(e.target.value)} className="form-control"></input>
                    </div>
                </div>
                <div className="card-footer">
                    <button type="submit" className="btn btn-primary">Login</button> or
                    <span> <Link to={'/register'} className="btn btn-success">Register </Link></span>
                </div>
            </div>
        </form>
    </div>
</div>
);
}

export default Login
